func (s *GotraxSuite) Test_EddsaSignature_roundtrips(c *C) {
	bb := [114]byte{0x42, 0x13, 0xAA}
	ed := CreateEddsaSignature(bb)

	c.Assert(ed.s, DeepEquals, bb)
	c.Assert(ed.S(), DeepEquals, bb)
}

func (s *GotraxSuite) Test_CreatePublicKey_createsAKey(c *C) {
	k1 := DeriveKeypair(sharedSym).Pub
	kk := CreatePublicKey(k1.k, SharedPrekeyKey)
	c.Assert(kk.k, DeepEquals, k1.k)
	c.Assert(kk.keyType, Equals, SharedPrekeyKey)
}

func (s *GotraxSuite) Test_CreatePrivateKey_createsAKey(c *C) {
	k1 := DeriveKeypair(sharedSym).Priv
	kk := CreatePrivateKey(k1.k)
	c.Assert(kk.k, DeepEquals, k1.k)
}
